#1er choix
def nb_of_journalist(array)
    puts array.length
end

#2eme choix
def nb_of_contain_nb(array)
    nb = 0
    array.each do |elt|
        elt.split("").each do |i| #POURQUOI LE elt.each FONCTONNE PAS !!!
            if i == "0" or i == "1" or i == "2" or i == "3" or i == "4" or i == "5" or i == "6" or i == "7" or i == "8" or i == "9" #bon pas tres opti j avoue
                nb += 1
                break
            end
        end
    end
    return nb
end

#3eme choix
def nb_of_letter_suite(array, letter_array)
    nb = 0
    array.each do |elt|
        u = 0
        elt.split("").each do |i|
            if i.downcase == letter_array[u]
                u += 1
                if u == letter_array.length 
                    nb += 1
                    u = 0
                end
            else
                u = 0
            end
        end
    end
    return nb
end

#4eme choix
def nb_of_begin_with_uppercase(array)
    nb = 0
    array.each do |elt|
        if elt[1] == elt[1].upcase
            nb += 1
        end
    end
    return nb
end

#5eme choix
def nb_of_contain_maj(array)
    nb = 0
    array.each do |elt|
        elt.split("").each do |i|
            if i == i.upcase
                nb += 1
                break
            end
        end
    end
    return nb
end

#6eme choix
def nb_of_(array)
    nb = 0
    array.each do |elt|
        elt.split("").each do |i|
            if i == "_"
                nb += 1
            end
        end
    end
    return nb
end

#7eme choix
def sort(array)
    array = array.sort
    return array
end

#8eme choix
def most_short_handle(array, nb)
    short_array = []
    array.each do |elt|
        if short_array.length < nb
            short_array << elt
        else
            short_array.each do |short_elt|
                if short_elt.length > elt.length
                    short_array << elt
                    short_array.delete(short_elt)
                    break
                end
            end
        end
    end
    return short_array
end

#9eme choix
def position_of(array, elt)
    return array.index(elt) + 1
end


#le menu
def menu(array)
    continu = true
    while continu
        puts "Menu :"
        puts "      - Combien y a-t-il de journalistes dans ce array ? (Tapper 1)"
        puts "      - Combien d'handle contiennent un numéro ? (Tapper 2)"
        puts "      - Combien d'handle contiennent les 4 lettres du prénom 'Aude' à la suite (sans prendre en compte les majuscules) ? (Tapper 3)"
        puts "      - Combien commencent par une majuscule (première lettre juste après le @) ? (Tapper 4)"
        puts "      - Combien contiennent au moins une majuscule ? (Tapper 5)"
        puts "      - Combien y a-t-il de underscore _ dans tous les pseudos confondus ? (Tapper 6)"
        puts "      - Trie la liste de handle par ordre alphabétique. (Tapper 7)"
        puts "      - Quels sont les 50 handles les plus courts de ce array ? (Tapper 8)"
        puts "      - Quelle est la position dans l'array de la personne @epenser ? (Tapper 9)"
        puts "      - Quitter. (Tapper 10)"
        print "> "
        choice = gets.chomp.to_i

        if choice == 1
            puts nb_of_journalist(array)
        elsif choice == 2
            puts nb_contain_nb(array)
        elsif choice == 3
            puts nb_of_letter_suite(array, ["a", "u", "d", "e"])
        elsif choice == 4
            puts nb_of_begin_with_uppercase(array)
        elsif choice == 5
            puts nb_of_contain_maj(array)
        elsif choice == 6
            puts nb_of_(array)
        elsif choice == 7
            puts sort(array)
        elsif choice == 8
            puts most_short_handle(array, 50)
        elsif choice == 9
            puts position_of(array, "@epenser")
        elsif choice == 10
            puts "Bonne journée !"
            continu = false
        end
    end
end


#le programme
menu(journalist_name)